sys_variable_value.value:
(function execute(inputs, outputs) {
  var eventCode = 'sns-xray-get-watch';
  try {
    var xmu = new XrayMetricUtil();
    outputs.payload = xmu.postHeapMetrics(inputs.jpdUrl, eventCode);
  } catch (err) {
    gs.error(err.getErrorMsg());
  }
})(inputs, outputs);

